CREATE TABLE `blood_bank`.`donor_info` (`D_id` INT NOT NULL AUTO_INCREMENT , `D_Name` VARCHAR(50) NOT NULL , `D_Gender` VARCHAR(10) NOT NULL , `D_BloodG` VARCHAR(5) NOT NULL , `D_Contact_No` VARCHAR(15) NOT NULL , `D_Email_Id` VARCHAR(30) NOT NULL , `D_Address` VARCHAR(50) NOT NULL , PRIMARY KEY (`D_id`)) ENGINE = InnoDB CHARSET=latin1 COLLATE latin1_swedish_ci;

CREATE TABLE `blood_bank`.`seeker_info` (`S_ID` INT NOT NULL AUTO_INCREMENT , `S_NAME` VARCHAR(40) NOT NULL , `S_CONTACT` VARCHAR(15) NOT NULL , `P_NAME` VARCHAR(40) NOT NULL , `P_DOB` DATE NOT NULL , `P_GENDER` VARCHAR(10) NOT NULL , `P_BG` VARCHAR(5) NOT NULL , `HOSPITAL_NAME` VARCHAR(40) NOT NULL , `HOSPITAL_ADD` VARCHAR(50) NOT NULL , PRIMARY KEY (`S_ID`)) ENGINE = InnoDB CHARSET=latin1 COLLATE latin1_swedish_ci;

CREATE TABLE `blood_bank`.`hospital_info` (`H_ID` INT NOT NULL AUTO_INCREMENT , `H_NAME` VARCHAR(40) NOT NULL , `H_CONTACT` VARCHAR(15) NOT NULL , `H_EMAIL` VARCHAR(30) NOT NULL , `H_ADD` VARCHAR(50) NOT NULL , `BLOOD_REQ_DATE` DATE NOT NULL , `REQ_BLOOD_GP` VARCHAR(5) NOT NULL , `NO_OF_UNITS` INT(4) NOT NULL , PRIMARY KEY (`H_ID`)) ENGINE = InnoDB CHARSET=latin1 COLLATE latin1_swedish_ci;

CREATE TABLE `blood_bank`.`employee_info` (`EMP_ID` INT NOT NULL AUTO_INCREMENT , `EMP_NAME` VARCHAR(35) NOT NULL , `EMP_GENDER` VARCHAR(10) NOT NULL , `EMP_BG` VARCHAR(5) NOT NULL , `EMP_CONTACT` VARCHAR(15) NOT NULL , `EMP_EMAIL` VARCHAR(30) NOT NULL , `EMP_ADD` VARCHAR(50) NOT NULL , `EMP_USER_NAME` VARCHAR(15) NOT NULL , `EMP_PASS` VARCHAR(15) NOT NULL , `EMP_CPASS` VARCHAR(15) NOT NULL , PRIMARY KEY (`EMP_ID`)) ENGINE = InnoDB CHARSET=latin1 COLLATE latin1_swedish_ci;

